-- GHOST specificCREATE OR REPLACE FUNCTION todos.trf_create_post_do ()RETURNS trigger AS$body$BEGIN    INSERT INTO todos.do (         dorecid,        dorecuuid,        dccode     )     SELECT              NEW.id,           NEW.uuid,           'DO'      FROM todos.do;    RETURN NEW;END;$body$LANGUAGE 'plpgsql'VOLATILECALLED ON NULL INPUTSECURITY INVOKERCOST 100;CREATE TRIGGER todos.tr_posts_after_insert AFTER INSERT ON "posts" FOR EACH STATEMENT EXECUTE PROCEDURE trf_create_post_do();